  text-align: center;
  padding: 2em 0;
}
.supporter {
  flex: 1 1 0;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  height: 50%;
}

.surpporter-wrapper {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  height: 10em;
}
.vpea {
  background-image: url(/4b7ac66f1732ae4bce724a71be122682.jpeg);
}

.telecom {
  background-image: url(/ba13dd67994e946e4b3f54ba160a9a4a.jpeg);
}

.rbc {
  background-image: url(/ea57738d3b99fa6267ac69f6faadcd14.png);
}

/* Footer */
.footer {
  height: 300px;
  background-color: #e9492e;
}
.contact-container {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

.social {
  font-size: 2rem;
}
.social-links {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  list-style: none;
}

