import ScrollOut from 'scroll-out';

const h1 = document.getElementsByTagName('h1');

// ScrollOut({
//   onShown: () => {
//     // use the web animation API

//     Array.prototype.forEach.call(h1, el =>
//       el.animate([{ opacity: 0 }, { opacity: 1 }], 1000)
//     );
//   },
//   onHidden: () => {
//     // hide the element initially
//     Array.prototype.forEach.call(h1, el => (el.style.opacity = 0));
//   }
// });

const headings = document.querySelectorAll('.content--sub-heading');
const paras = document.querySelectorAll('.content--paragraph');

const mainNav = document.querySelector('.main-nav');
const navBarToggle = document.querySelector('.navbar-toggle');

navBarToggle.addEventListener('click', function() {
  mainNav.classList.toggle('active');
});

function toggleContent(headings, paras, length) {
  for (let i = 0; i < length; i++) {
    headings[i].addEventListener('click', () => toggle(paras[i]));
  }
}

toggleContent(headings, paras, headings.length);

// Show an element
const show = function(elem) {
  // Get the natural height of the element
  const getHeight = function() {
    elem.style.display = 'block'; // Make it visible
    const height = elem.scrollHeight + 'px'; // Get it's height
    elem.style.display = ''; //  Hide it again
    return height;
  };

  const height = getHeight(); // Get the natural height
  elem.classList.add('is-visible'); // Make the element visible
  elem.style.height = height; // Update the max-height

  // Once the transition is complete, remove the inline max-height so the content can scale responsively
  window.setTimeout(function() {
    elem.style.height = '';
  }, 350);
};

// Hide an element
const hide = function(elem) {
  // Give the element a height to change from
  elem.style.height = elem.scrollHeight + 'px';

  // Set the height back to 0
  window.setTimeout(function() {
    elem.style.height = '0';
  }, 1);

  // When the transition is complete, hide it
  window.setTimeout(function() {
    elem.classList.remove('is-visible');
  }, 350);
};

// Toggle element visibility
const toggle = function(elem, timing) {
  // If the element is visible, hide it
  if (elem.classList.contains('is-visible')) {
    hide(elem);
    return;
  }

  // Otherwise, show it
  show(elem);
};

function smoothScroll(target, duration) {
  var target = document.querySelector(target);
  var targetPosition = target.getBoundingClientRect().top;
  var startPosition = window.pageYOffset;
  var distance = targetPosition - startPosition;
  let startTime = null;

  function animation(currentTime) {
    if (startTime === null) {
      startTime = currentTime;
    }

    var timeElapsed = currentTime - startTime;
    var run = ease(timeElapsed, startPosition, distance, duration);
    window.scrollTo(0, run);
    if (timeElapsed < duration) {
      requestAnimationFrame(animation);
    }
  }

  function ease(t, b, c, d) {
    t /= d / 2;
    if (t < 1) return (c / 2) * t * t + b;
    t--;
    return (-c / 2) * (t * (t - 2) - 1) + b;
  }

  requestAnimationFrame(animation);
}

function scrollToPosition() {
  const about = document.querySelector('.nav--about');
  const service = document.querySelector('.nav--service');
  const contact = document.querySelector('.nav--contact');
  about.addEventListener('click', () => {
    smoothScroll('#about-us', 1000);
  });
  service.addEventListener('click', () => {
    smoothScroll('.service', 1000);
  });
  contact.addEventListener('click', () => {
    smoothScroll('.contact', 1000);
  });
}

scrollToPosition();

//button
const mybutton = document.getElementById('myBtn');

// When the user scrolls down 20px from the top of the document, show the button
window.onscroll = function() {
  scrollFunction();
};

function scrollFunction() {
  if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
    mybutton.style.display = 'block';
  } else {
    mybutton.style.display = 'none';
  }
}

// When the user clicks on the button, scroll to the top of the document
function topFunction() {
  document.body.scrollTop = 0; // For Safari
  document.documentElement.scrollTop = 0; // For Chrome, Firefox, IE and Opera
}

mybutton.addEventListener('click', () => topFunction());
