    rgba(231, 162, 29, 1) 100%
  );
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#e22809",endColorstr="#e7a21d",GradientType=1);
  @include flexColumn();
  justify-content: space-evenly;
}
.contact--container {
  width: 90vw;
  @include flexRow();
  justify-content: space-evenly;
  align-items: flex-end;
}
.social {
  font-size: 2rem;
}
.social--links {
  width: 90vw;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  list-style: none;
  .fab {
    font-size: 3rem;
    color: $color--font;
    cursor: pointer;
  }

