    justify-content: space-between;
    padding-bottom: 0;
    align-items: center;
    &-toggle {
      display: none;
    }
  }

  .main-nav {
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
  }

  .main-nav li {
    margin: 0;
  }
  .nav {
    display: flex;
  }
}

li {
  position: relative;
  color: inherit;
  text-decoration: none;
  line-height: 1.8em;
  &:before,
  &:after {
    content: '';
    position: absolute;
    transition: transform 0.3s ease;
  }
}
.hover-effect {
  padding: 1em;
  display: inline-block;
  overflow: hidden;
  &:before,
  &:after {
    left: 0;
    width: 100%;
    height: 1px;
    background: rgba(255, 255, 255, 0.7);
  }
  &:before {
    bottom: 0;
    transform: translateX(-100%);
  }
  &:after {
    top: 0;
    transform: translateX(100%);
  }
  &:hover:before,
  &:hover:after {
    transform: translateX(0);
  }
}
